sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	//Type appropriate comment here, and begin script below
	// Modified the if to return if the newValue == oldValue to avoid
	// unecessary trips to the server
	// Si está recién cargando, o el valor es vacío, o el valor no hay cambiado, salir de aquí.
	if (isLoading || newValue === '' || newValue == oldValue) {
		return;
	}

	var getEmailAddr = new GlideAjax('GetEmailAddress'); // Se especifica la clase de extension a usar de GlideAjax.
	// Specify the getEmail method
	getEmailAddr.addParam('sysparm_name','getEmail'); // Se especifica qué método de la extensión se va a usar.
	getEmailAddr.addParam('sysparm_userID',g_form.getValue('u_requested_for')); // Se le pasa el parámetro.

	// Send the request to the server
	getEmailAddr.getXML(populateEmailField);

	function populateEmailField(response) {
		// Extract the email address from the response, clear any value from the email field, 
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',emailFromScriptInclude);
	}

}
