var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {

    },
	esFechaPasada: function (strFecha) {
		var ahora = new GlideDateTime();
		var auxFecha = new GlideDateTime(strFecha);
		if (auxFecha.before(ahora)) {
			return true;
		} else {
			return false;
		}
	},
	esHoy : function (strFecha) {
		var ahora = new GlideDateTime();
		var diames_dehoy = ahora.getLocalDate(); // Obtiene fecha sin horario.
		var auxFecha = new GlideDateTime(strFecha);
		var diames_whenneeded = auxFecha.getLocalDate(); // Obtiene fecha sin horario.
		if (diames_dehoy.compareTo(diames_whenneeded) == 0) { // Si es el mismo día/mes/año, es hoy
			return true;
		} else {
			return false;
		}
	},
    type: 'NeedItUtils'
};
