sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// Define the getEmail function.  
	// Create a GlideRecord for the User table.
	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
	// Return the email address for the requested record
	getEmail: function() {
			var userRecord = new GlideRecord("sys_user"); // se prepara un objeto de tipo registro de usuario
			userRecord.get(this.getParameter('sysparm_userID')); // Se recibe el parámetro con el sys_id del usuario.
			return userRecord.email + ''; // Se devuelve el email del usuario. Le agrego '' para forzar conversión a string cuando email es nil.
	},
    type: 'GetEmailAddress'
});
